package conditionalstatements;

public class IfElseIfStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 30;
		if(age>=18)
		{
			System.out.println("Adult");
		}
		else if(age>=15)
		{
			System.out.println("Teen");
		}
		else
		{
			System.out.println("Child");
		}

	}

}
